    public override string StrategyId => "ai-sagemaker";public override string DisplayName => "AWS SageMaker";public override ConnectionStrategyCapabilities Capabilities => new(SupportsPooling: false, SupportsStreaming: true, SupportsAuthentication: true);public override string SemanticDescription => "AWS SageMaker managed ML platform. Real-time inference endpoints, batch transforms, and model deployment.";public override string[] Tags => ["aws", "sagemaker", "ml-platform", "inference", "model-deployment"];
    public AwsSageMakerConnectionStrategy(ILogger? logger = null) : base(logger) { }
    protected override async Task<IConnectionHandle> ConnectCoreAsync(ConnectionConfig config, CancellationToken ct){var region = config.Properties.GetValueOrDefault("Region", "us-east-1")?.ToString() ?? "us-east-1";var baseUrl = $"https://runtime.sagemaker.{region}.amazonaws.com";var httpClient = new HttpClient { BaseAddress = new Uri(baseUrl), Timeout = config.Timeout };return new DefaultConnectionHandle(httpClient, new Dictionary<string, object> { ["Provider"] = "SageMaker", ["Region"] = region });}
