    public override async Task<string> SendRequestAsync(IConnectionHandle handle, string prompt, Dictionary<string, object>? options = null, CancellationToken ct = default){var httpClient = handle.GetConnection<HttpClient>();var endpointName = options?.GetValueOrDefault("endpoint", "")?.ToString() ?? throw new ArgumentException("SageMaker endpoint name required in options");var contentType = options?.GetValueOrDefault("content_type", "application/json")?.ToString() ?? "application/json";var content = new StringContent(prompt, Encoding.UTF8, contentType);var response = await httpClient.PostAsync($"/endpoints/{endpointName}/invocations", content, ct);response.EnsureSuccessStatusCode();return await response.Content.ReadAsStringAsync(ct);}
    public override async IAsyncEnumerable<string> StreamResponseAsync(IConnectionHandle handle, string prompt, Dictionary<string, object>? options = null, [EnumeratorCancellation] CancellationToken ct = default){var httpClient = handle.GetConnection<HttpClient>();var endpointName = options?.GetValueOrDefault("endpoint", "")?.ToString() ?? throw new ArgumentException("SageMaker endpoint name required in options");var content = new StringContent(prompt, Encoding.UTF8, "application/json");var request = new HttpRequestMessage(HttpMethod.Post, $"/endpoints/{endpointName}/invocations-response-stream"){Content = content};var response = await httpClient.SendAsync(request, HttpCompletionOption.ResponseHeadersRead, ct);using var stream = await response.Content.ReadAsStreamAsync(ct);using var reader = new System.IO.StreamReader(stream);while (!reader.EndOfStream && !ct.IsCancellationRequested){var line = await reader.ReadLineAsync(ct);if (string.IsNullOrEmpty(line)) continue;string? chunk = null;try{var evt = JsonDocument.Parse(line);if (evt.RootElement.TryGetProperty("PayloadPart", out var payload) && payload.TryGetProperty("Bytes", out var bytes)){chunk = bytes.GetString();}}catch{chunk = line;}if (chunk != null) yield return chunk;}}
